mapper.select:
select * from orders


        and number=#{number}


        and phone=#{phone}


        and user_id=#{userId}


        and status=#{status}


        and order_time >= #{beginTime}


        and order_time <= #{endTime}


order by order_time desc

select date_format(order_time, '%Y-%m-%d') as orderDate,sum(amount) as orderMoney
from orders


        status=#{status}


        and order_time between #{beginTime} and #{endTime}


group by date_format(order_time, '%Y-%m-%d')
order by date_format(order_time, '%Y-%m-%d')

select date_format(order_time, '%Y-%m-%d') as orderDate,count(*) as orderCount
from orders


        status=#{status}


        and order_time between #{beginTime} and #{endTime}


group by date_format(order_time, '%Y-%m-%d')
order by date_format(order_time, '%Y-%m-%d')

select order_detail.name as name, count(*) as number from orders left join order_detail on orders.id = order_detail.order_id
status=#{status}
and order_time between #{beginTime} and #{endTime}
group by order_detail.name order by count(*) desc limit 0,10
